import servoimport vgaimport ntptimeimport timeZoneimport time__author__ = 'ebrecht'def settime():    # Get time from internet    ntptime.settime()    # Adjust time to time zone    timeZone.adjust_time()    return True# Write data to text windowprint('Start BusTimes')# Initialize allsv = servo.servo()while not settime():    print('Keine Zeit bekommen')    sv.setToFail()    time.sleep(10)print('Time is set')print(time.localtime())lastUpdate = time.time()while True:    # Time accurate?    if time.time() - lastUpdate > 3600:        lastUpdate = time.time()        while not settime():            print('Keine Zeit bekommen')            sv.setToFail()            time.sleep(10)    # Get bus times from VGA    timesList = vga.get_times()    while not timesList:        print('Keine Abfahrtzeiten bekommen')        sv.setToFail()        time.sleep(10)        timesList = vga.get_times()    # is list empty?    while timesList:        print(timesList)        # Use 1. entry        minutes = 2        while minutes > 1:            actualTime = time.localtime()            # how many minutes left?            minutes = vga.get_minutes(actualTime, timesList[0])            # set servo            sv.minutes(minutes)        #  delete used time        del timesList[0]    pass