__author__ = 'ebrecht'MINUTES_FAIL = 0MINUTES_LONG = 255class servo:    def __init__(self):        self.minutes = MINUTES_LONG        print ('Init to Minutes : ' + str(self.minutes))        pass    def setToFail(self):        if self.minutes != MINUTES_FAIL:            self.minutes = MINUTES_FAIL            print ('Minutes : Fail')        pass    def setToLong(self):        if self.minutes != MINUTES_LONG:            self.minutes = MINUTES_LONG            print ('Minutes : ToLong')        pass    def time(self, minutes):        if self.minutes != minutes:            self.minutes = minutes            print ('Minutes : ' + str(self.minutes))