__author__ = 'ebrecht'try:    import ujson as jsonexcept:    import jsontry:    import utime as timeexcept:    import timetry:    import urllib.urequest as urllibreqexcept:    import urllib.request as urllibreqdef get_time_zone(time_s, longitude=10.900556, latitude=48.338056):    try:        url = 'https://maps.googleapis.com/maps/api/timezone/json'        url_parameter = 'timestamp={0:f}&location={1:f},{2:f}'.format(time_s, latitude, longitude)        full_url = url + '?' + url_parameter        con = urllibreq.urlopen(full_url)        data = con.read().decode('UTF-8')        del con        return json.loads(data)    except:        return {'status': 'INVALID_REQUEST'}def adjust_time():    import machine    ti_zn = get_time_zone(time.time())    if ti_zn['status'] is 'OK':        tm = time.localtime(time.time() + ti_zn['rawOffset'] + ti_zn['dstOffset'])        tm = tm[0:3] + (0,) + tm[3:6] + (0,)        machine.RTC().datetime(tm)        return True    else:        return Falseif __name__ == "__main__":    # Show window    print('wait...')    print(get_time_zone(1478708042.6730614))